CREATE TABLE wallets(
	id SERIAL PRIMARY KEY,
	name TEXT,
	adress TEXT,
	sum REAL,
	user_id INT
);


CREATE TABLE tokens(
	id SERIAL PRIMARY KEY,
	asset TEXT,
	address TEXT,
	balance REAL,
	price REAL,
	value REAL,
	track BOOl,
	delta REAL, 
	network_id INT, 
	user_id INT,
	wallet_id INT           
);

